import React from 'react';
import "./services.css";
import Image1 from "../../assets/service-1.svg";
import Image2 from "../../assets/service-2.svg";
import Image3 from "../../assets/service-3.svg";

import pb from '../../pocketbase'; 
import { useState, useEffect } from 'react';

const data = [
  {
    id: 1,
    image: Image1,
    title: "UI/UX design",
    description:
      "Lorem ipsum dolor sit amet consectetuer adipiscing elit         aenean commodo ligula eget.",
  },
  {
    id: 2,
    image: Image2,
    title: "Web Development",
    description:
      "Lorem ipsum dolor sit amet consectetuer adipiscing elit         aenean commodo ligula eget.",
  },
  {
    id: 3,
    image: Image3,
    title: "Photography",
    description:
      "Lorem ipsum dolor sit amet consectetuer adipiscing elit         aenean commodo ligula eget.",
  },
];


const Services = () => {

  const [services, setServices] = useState([]);

  useEffect(() => {
    const fetchServices = async () => {
      try {
        const records = await pb.collection('ServiceData').getFullList();

        // Append image URL to each record
        const servicesWithUrls = records.map((record) => ({
          id: record.id,
          title: record.title,
          description: record.description,
          background: record.background,
          image: pb.files.getURL(record, record.image),
        }));

        setServices(servicesWithUrls);
      } catch (error) {
        console.error('Error fetching ServiceData:', error);
      }
    };

    fetchServices();
  }, []);


  return (
    <section className="services container section" id="services">
      <h2 className="section__title">Services</h2>
      <div className="services__container grid">

        {services.map(({ id, image, title, description }) => (
          <div className="services__card" key={id} style={{backgroundColor: background}}>
            <img src={image} alt={title} className="services__img" />
            <h3 className="services__title">{title}</h3>
            <p className="services__description">{description}</p>
          </div>
        ))}

        {/* {data.map(({id, image, title, description}) => {
