import React from 'react';
import "./about.css";
import Image from "../../assets/avatar-2.svg";
import AboutBox from './AboutBox';

import pb from "../../pocketbase";
import { useState, useEffect } from 'react';


const About = () => {

    const [avatarUrl, setavatarUrl] = useState("");
  useEffect(() => {

    //Fetching My Avatar
    const fetchLogo = async () => {
      try {
        // Get the record where Name == "Avatar"
        const record = await pb.collection("HomeImages").getFirstListItem('Name="Me"');

        // Construct image URL using PocketBase SDK
        const url = pb.files.getURL(record, record.Image);
        setavatarUrl(url);
      } catch (error) {
        console.error("Error fetching logo:", error);
      }
    };

    fetchLogo();

  }, []);

  return (
    <section className="about container" id="about">
      <h2 className="section__title">About Me</h2>

      <div className="about__container grid">
        <img src={avatarUrl} alt="" className="about__img" />

        <div className="about__data grid">
          <div className="about__info">
            <p className="about__description">I am Shiva Dhali, a front end developer from India. I have rich experience in web development.</p>
            <a href="" className="btn">Download CV</a>
          </div>

          <div className="about__skills grid">
            <div className="skills__data">
              <div className="skills__titles">
                <h3 className="skills__name ">Development</h3>
                <span className="skills__number ">90%</span>
              </div>

              <div className="skills__bar">
                <span className="skills__percentage development"></span>
              </div>
            </div>

            <div className="skills__data">
              <div className="skills__titles">
                <h3 className="skills__name">UI/UX Design</h3>
                <span className="skills__number">80%</span>
              </div>

              <div className="skills__bar ">
                <span className="skills__percentage ui__design"></span>
              </div>
            </div>

            <div className="skills__data">
              <div className="skills__titles">
                <h3 className="skills__name">Photography</h3>
                <span className="skills__number">60%</span>
              </div>

              <div className="skills__bar">
                <span className="skills__percentage photography"></span>
              </div>
            </div>
          </div>
        </div>
      </div>

      <AboutBox />
    </section>
  )
};

export default About
