import React from 'react';
import { useState, useEffect } from 'react';
import "./home.css";
import Me from "../../assets/avatar-1.svg"
import HeaderSocial from './HeaderSocial';
import ScrollDown from './ScrollDown';
import Shapes from './Shapes';

import pb from "../../pocketbase";


const Home = () => {

  const [avatarUrl, setavatarUrl] = useState("");

  useEffect(() => {
    const fetchLogo = async () => {
      try {
        // Get the record where Name == "Logo"
        const record = await pb.collection("Home").getFirstListItem('Name="Me"');

        // Construct image URL using PocketBase SDK
        const url = pb.files.getURL(record, record.Image);
        setavatarUrl(url);
      } catch (error) {
        console.error("Error fetching logo:", error);
      }
    };

    fetchLogo();
  }, []);

  return (
    <section className="home container" id="home">
      <div className="intro">
        <img src={avatarUrl} alt="" className="home__img" />
        <h1 className="home__name">Shiva Dhali</h1>
        <span className="home__education">I am a Front-End Developer</span>
        <HeaderSocial />

        <a href="#contact" className="btn">Hire Me</a>

        <ScrollDown />
      </div>

      <Shapes />
    </section>
  )
}

export default Home
